    def cargarImagen(self):
        fileName, _ = QFileDialog.getOpenFileName(self,"Buscar Im√°genes", "","Archivos PNG (*.png);; Archivos JPG (*.jpg);; Archivos JPEG (*.jpeg)")
        if fileName:
            self.fn = fileName
            self.visualizacionDeImagen.setPixmap(QtGui.QPixmap(fileName))

    def predecirImagen(self):
        image_299 = load_img(self.fn,target_size= (299, 299))
        image_224 = load_img(self.fn,target_size= (224, 224))

        image_array_299 = img_to_array(image_299) / 255
        image_array_299 = np.array([image_array_299])

        image_array_224 = img_to_array(image_224) / 255
        image_array_224 = np.array([image_array_224])

        prediccion_modelo_personalizado = self.modelo_personalizado.predict(image_array_299) * 100
        prediccion_modelo_vgg16 = self.modelo_vgg16.predict(image_array_224) * 100
        prediccion_modelo_vgg19 = self.modelo_vgg19.predict(image_array_224) * 100
        prediccion_modelo_resnet50 = self.modelo_resnet50.predict(image_array_224) * 100

        if np.argmax(prediccion_modelo_personalizado[0]) <= 1:
            self.label_rad_tac_mp.setText("Radiograf√≠a")
            self.resultadoCovidMP.setText(str(round(prediccion_modelo_personalizado[0][0],2)) + "%")
            self.resultadoNormalMP.setText(str(round(prediccion_modelo_personalizado[0][1],2)) + "%")
        else:
            self.label_rad_tac_mp.setText("TAC")
            self.resultadoCovidMP.setText(str(round(prediccion_modelo_personalizado[0][2],2)) + "%")
            self.resultadoNormalMP.setText(str(round(prediccion_modelo_personalizado[0][3],2)) + "%")

        if np.argmax(prediccion_modelo_vgg16[0]) <= 1:
            self.label_rad_tac_vgg16.setText("Radiograf√≠a")
            self.resultadoCovidVGG16.setText(str(round(prediccion_modelo_vgg16[0][0],2)) + "%")
            self.resultadoNormalVGG16.setText(str(round(prediccion_modelo_vgg16[0][1],2)) + "%")
        else:
            self.label_rad_tac_vgg16.setText("TAC")
            self.resultadoCovidVGG16.setText(str(round(prediccion_modelo_vgg16[0][2],2)) + "%")
            self.resultadoNormalVGG16.setText(str(round(prediccion_modelo_vgg16[0][3],2)) + "%")

        if np.argmax(prediccion_modelo_vgg19[0]) <= 1:
            self.label_rad_tac_vgg19.setText("Radiograf√≠a")
            self.resultadoCovidVGG19.setText(str(round(prediccion_modelo_vgg19[0][0],2)) + "%")
            self.resultadoNormalVGG19.setText(str(round(prediccion_modelo_vgg19[0][1],2)) + "%")
        else:
            self.label_rad_tac_vgg19.setText("TAC")
            self.resultadoCovidVGG19.setText(str(round(prediccion_modelo_vgg19[0][2],2)) + "%")
            self.resultadoNormalVGG19.setText(str(round(prediccion_modelo_vgg19[0][3],2)) + "%")

        if np.argmax(prediccion_modelo_resnet50[0]) <= 1:
            self.label_rad_tac_resnet50.setText("Radiograf√≠a")
            self.resultadoCovidResNet.setText(str(round(prediccion_modelo_resnet50[0][0],2)) + "%")
            self.resultadoNormalResNet.setText(str(round(prediccion_modelo_resnet50[0][1],2)) + "%")
        else:
            self.label_rad_tac_resnet50.setText("TAC")
            self.resultadoCovidResNet.setText(str(round(prediccion_modelo_resnet50[0][2],2)) + "%")
            self.resultadoNormalResNet.setText(str(round(prediccion_modelo_resnet50[0][3],2)) + "%")

        print(np.argmax(prediccion_modelo_personalizado[0]))

